window.digirisk.gallery.keyup = function( event ) {
	if ( event.keyCode == 37 ) {
		jQuery( '.wpeo-gallery .prev' ).click();
	}
	else if ( event.keyCode == 39 ) {
		jQuery( '.wpeo-gallery .next' ).click();
	}
	else if ( event.keyCode == 27 ) {
		jQuery( '.wpeo-gallery .close' ).click();
	}
};

